function ocultaElemento(){document.querySelector("header").classList.remove("header--is-hidden")}function exbibeElemento(){document.querySelector(".header").classList.add("header--is-hidden")}function abrefecharesp(e){console.log(e),e.target.parentNode.classList.toggle("faq__questions__item--is-open")}function removebotaoativo(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__tabs__button--is-active")}function escondeAbas(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("shows__list--is-active")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]"),o=document.querySelectorAll("[data-faq-question]");const e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<e?ocultaElemento:exbibeElemento)()});for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){var t=e.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`);escondeAbas(),t.classList.add("shows__list--is-active"),removebotaoativo(),e.target.classList.add("shows__tabs__button--is-active")});for(let e=0;e<o.length;e++)o[e].addEventListener("click",abrefecharesp)});